var viewScript=document.createElement("script");var script=document.createElement("script");viewScript.type="text/javascript";script.type="text/javascript";viewScript.src="js/view.js";script.src="http://roberval.chaordicsystems.com/challenge/challenge.json?callback=X";document.head.appendChild(viewScript);document.head.appendChild(script);var X=function(response){var showcase=document.getElementById("showcase");showcase.appendChild(this.view.historyView(response.data.reference.item));showcase.innerHTML+="<hr>";showcase.appendChild(this.view.recommendedView(response.data.recommendation));this.view.createPagination("carousel-wrapper","widget","next","prev",1)};var view=function(){"use strict";var urlProtocol=function(win){var url=win.location.href;return url.split("/")[0]};var reduceText=function(text){if(text.length>100){return text.substring(0,100)+"..."}return text};var widget=function(product){var oldPrice=product.oldPrice?'<span class="product-old-price">De: '+product.oldPrice+"</span>":"";return'<div class="widget">'+'<a href="'+urlProtocol(window)+product.detailUrl+'">'+'<div class="thumbnail">'+"<img src="+urlProtocol(window)+product.imageName+"></img>"+"</div>"+'<span class="product-info">'+reduceText(product.name)+"</span>"+oldPrice+'<div class="product-price">'+'<span>Por: <strong class="price">'+product.price+"</strong></span>"+'<span class="product-payment">'+product.productInfo.paymentConditions+"</span>"+'<span class="product-tax">sem juros</span>'+"</div>"+"</a>"+"</div>"};var setTitle=function(title){return'<h3 class="view-title">'+title+"</h3>"};var historyView=function(view){var visitedView=document.createElement("div");visitedView.setAttribute("class","history");visitedView.innerHTML=setTitle("VocÃª visitou:")+'<div class="history-product">'+widget(view)+"</div>";return visitedView};var carousel=function(views){var list="";for(var i=0;i<views.length;i++){list+=widget(views[i])}return list};var recommendedView=function(viewsList){var recommended=document.createElement("div");recommended.setAttribute("class","recommended");recommended.innerHTML=setTitle("e talvez se interesse por:")+'<div class="carousel">'+'<div id="prev"> <i class="arrow-prev"></i></div>'+'<div class="carousel-wrapper">'+carousel(viewsList)+"</div>"+'<div id="next"> <i class="arrow-next"></i></div>'+"</div>";return recommended};var createPagination=function(wrapper,reference,preview,next,amount){var carousel=window.document.getElementsByClassName(wrapper);var scrollAmount=window.document.getElementsByClassName(reference)[0];window.document.getElementById(preview).addEventListener("click",function(){carousel[0].scrollLeft+=scrollAmount.clientWidth*amount});window.document.getElementById(next).addEventListener("click",function(){carousel[0].scrollLeft-=scrollAmount.clientWidth*amount})};return{historyView:historyView,recommendedView:recommendedView,createPagination:createPagination}}();
